import React, { Component } from "react";
import "./Portfolio.css";
import NavBar from "../navbar/NavBar";
import FirstSection from "./first section/FirstSection";

class Portfolio extends Component {
  state = { activeSection: 0 };

  handleFilterClick = menuOption => {
    this.setState({ activeSection: 1 });
  };

  render() {
    const props = this.props;

    return (
      <div className="portfolio">
        <div className="portfolio-content">
          <span>{props.home.description}</span>
          <NavBar
            navCls="filters"
            navOptionCls="filters-option"
            navDotCls="filters-option-dot"
            onNavOptionClick={this.handleFilterClick}
            options={props.home ? props.home.sections : []}
          />
          {this.state.activeSection === 0 ? (
            <FirstSection
              images={props.home ? props.home.sections[0].images : []}
            />
          ) : null}
          {this.state.activeSection === 1 ? (
            <div className="section-services"></div>
          ) : null}
        </div>
      </div>
    );
  }
}

export default Portfolio;
