import React, { Component } from "react";
import "./Header.css";
import NavBar from "../navbar/NavBar";
import SearchIcon from "./white-search-icon.jpg";

class Header extends Component {
  state = { activeSlide: 0 };

  render() {
    const state = this.state,
      props = this.props,
      slides = props.slides;

    return (
      <div className="header">
        <div>
          <div>
            {" "}
            <NavBar
              navCls="navbar"
              navOptionCls="navbar-option"
              navDotCls="navbar-option-dot"
              options={props.menuOptions}
            />
          </div>

          <div>
            <img
              className="navbar-search-icon"
              height="23"
              width="23"
              alt="search"
              src={SearchIcon}
            ></img>
          </div>
        </div>
        <span className="slider-title">
          {slides.length ? slides[state.activeSlide].title : ""}
        </span>
        <span className="slider-subtitle">
          {slides.length ? slides[state.activeSlide].subtitle : ""}
        </span>
        <div className="slider-dot-holder">
          {slides.length
            ? slides.map((slide, index) => {
                return (
                  <div className="slider-dot" key={"slider-dot-" + index}></div>
                );
              })
            : null}
        </div>
      </div>
    );
  }
}

export default Header;
