import React, { Component } from "react";
import axios from "axios";
import Header from "../home/header/Header";
import Portfolio from "../home/portfolio/Portfolio";
import "./Home.css";

class Home extends Component {
  state = { slides: [], sections: [], menuOptions: [] };

  componentDidMount() {
    this.getData();
  }

  async getData() {
    let responses = [];
    let homeUrl = "https://voda-react-assessment.herokuapp.com/home";
    let sliderUrl = "https://voda-react-assessment.herokuapp.com/slider";
    let menuUrl = "https://voda-react-assessment.herokuapp.com/menu";

    const requestHome = axios.get(homeUrl);
    const requestSlider = axios.get(sliderUrl);
    const requestMenu = axios.get(menuUrl);
    debugger;
    try {
      responses = await axios.all([requestHome, requestSlider, requestMenu]);

      this.setState({ sections: responses[0].data });
      this.setState({ slides: responses[1].data });
      this.setState({ menuOptions: responses[2].data });
    } catch (err) {
      console.error(err);
    }
  }

  getSlides() {
    let slides = this.sendRequest("slider") || [];

    this.setState({ slides: slides });
  }

  getSections() {
    let sections = this.sendRequest("home") || [];

    this.setState({ sections: sections });
  }

  getMenuOptions() {
    let menuOptions = this.sendRequest("menu") || [];

    this.setState({ menuOptions: menuOptions });
  }

  async sendRequest(endpoint) {}
  render() {
    const state = this.state;

    return (
      <div className="home">
        <Header slides={state.slides} menuOptions={state.menuOptions} />
        <Portfolio sections={state.sections} />
      </div>
    );
  }
}

export default Home;
