import React, { Component } from "react";
import "./Header.css";

class Header extends Component {
  state = { activeSlide: 0 };

  render() {
    const state = this.state,
      props = this.props,
      slides = props.slides;

    return (
      <div className="header">
        <span className="slider-title">
          {slides.length ? slides[state.activeSlide].title : ""}
        </span>
        <span className="slider-subtitle">
          {slides.length ? slides[state.activeSlide].subtitle : ""}
        </span>
        <div className="slider-dot-holder">
          {slides.length
            ? slides.map((slide, index) => {
                return (
                  <div className="slider-dot" key={"slider-dot-" + index}></div>
                );
              })
            : null}
        </div>
      </div>
    );
  }
}

export default Header;
