import React, { Component } from "react";
import axios from "axios";
import SearchIcon from "./white-search-icon.jpg";
import "./NavBar.css";

class Navbar extends Component {
  state = { options: [] };

  componentDidMount() {
    this.getMenuOptions();
  }

  async getMenuOptions() {
    let response;

    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/menu"
      );
      this.setState({ options: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  generateId() {
    return Math.random()
      .toString(36)
      .substr(2, 9);
  }

  render() {
    return (
      <nav className="navbar">
        <div>
          <ul>
            {this.state.options.map(option => {
              return (
                <li className="navbar-option" key={this.generateId()}>
                  {option.title}
                  <div className="navbar-option-dot"></div>
                </li>
              );
            })}
          </ul>
        </div>
        <div>
          <img
            className="navbar-search-icon"
            height="23"
            width="23"
            alt="search"
            src={SearchIcon}
          ></img>
        </div>
      </nav>
    );
  }
}

export default Navbar;
