import React, { Component } from "react";
import axios from "axios";
import "./Header.css";
import NavBar from "./navbar/NavBar";

class Header extends Component {
  state = { slides: [], activeSlide: 0 };

  componentDidMount() {
    this.getSlides();
  }

  async getSlides() {
    let response;
    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/slider"
      );
      this.setState({ slides: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  render() {
    return (
      <div className="header">
        <NavBar options={this.state.menuOptions} />
        <span>{this.state.slides[this.state.activeSlide].title}</span>
      </div>
    );
  }
}

export default Header;
