import React, { Component } from "react";
import axios from "axios";
import Header from "../home/header/Header";
import Portfolio from "../home/portfolio/Portfolio";
import "./Home.css";

class Home extends Component {
  state = { slides: [], sections: [], menuOptions: [] };

  componentDidMount() {
    this.getSlides();
    this.getSections();
    this.getMenuOptions();
  }

  async getSlides() {
    let slides = await this.sendRequest("slider");
    debugger;
    this.setState({ slides: slides });
  }

  getSections() {
    let sections = this.sendRequest("home") || [];

    this.setState({ sections: sections });
  }

  getMenuOptions() {
    let menuOptions = this.sendRequest("menu") || [];

    this.setState({ menuOptions: menuOptions });
  }

  sendRequest(endpoint) {
    let response;

    try {
      response = axios.get(
        `https://voda-react-assessment.herokuapp.com/${endpoint}`
      );
      return response.data;
    } catch (err) {
      console.error(err);
      return false;
    }
  }
  render() {
    const state = this.state;

    return (
      <div className="home">
        <Header slides={state.slides} menuOptions={state.menuOptions} />
        <Portfolio sections={state.sections} />
      </div>
    );
  }
}

export default Home;
