import React, { Component } from "react";
import axios from "axios";
import "./Header.css";
import NavBar from "./navbar/Navbar";

class Header extends Component {
  state = { menuOptions: [] };

  async componentDidMount() {
    this.getMenuOptions();
  }

  async getMenuOptions() {
    let response;
    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/menu"
      );
      this.setState({ menuOptions: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  render() {
    return (
      <div className="header">
        <NavBar options={this.state.menuOptions} />
        <nav className="navbar">
          <div>
            <ul>
              {this.state.menuOptions.map(menuOption => {
                return (
                  <li className="navbar-option">
                    <a>
                      {menuOption.title}
                      <div className="navbar-option-dot"></div>
                    </a>
                  </li>
                );
              })}
            </ul>
          </div>
          <div>
            <img
              height="45"
              width="45"
              src="http://endlessicons.com/wp-content/uploads/2012/12/search-icon.png"
            ></img>
          </div>
        </nav>
      </div>
    );
  }
}

export default Header;
