import React, { Component } from "react";
import axios from "axios";
import Header from "./header/Header";
import Home from "./home/Home";
import Page1 from "./page_1/Page1";
import "./App.css";

class App extends Component {
  state = {
    home: false,
    slides: [],
    filters: [
      { title: "Section 1", id: 0 },
      { title: "Section 2", id: 1 }
    ],
    menuOptions: [],
    activePage: 0
  };

  componentDidMount() {
    this.getData();
  }

  async getData() {
    let responses = [],
      homeUrl = "https://voda-react-assessment.herokuapp.com/home",
      sliderUrl = "https://voda-react-assessment.herokuapp.com/slider",
      menuUrl = "https://voda-react-assessment.herokuapp.com/menu";

    const requestHome = axios.get(homeUrl);
    const requestSlider = axios.get(sliderUrl);
    const requestMenu = axios.get(menuUrl);

    try {
      responses = await axios.all([requestHome, requestSlider, requestMenu]);

      this.setState({ home: responses[0].data[0] });
      this.setState({ slides: responses[1].data });
      this.setState({ menuOptions: responses[2].data });
    } catch (err) {
      console.error(err);
    }
  }

  getActivePage() {
    let state = this.state,
      activePage = state.activePage;

    switch (activePage) {
      case 0:
        return <Home home={state.home} filters={state.filters} />;
      case 1:
        return <Page1 />;
    }
  }

  render() {
    const state = this.state;

    return (
      <div className="App">
        <Header slides={state.slides} menuOptions={state.menuOptions} />
        {this.getActivePage()}
      </div>
    );
  }
}

export default App;
