import React, { Component } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import axios from "axios";
import Header from "./components/app/header/Header";
import Home from "./components/app/home/Home";
import Page1 from "./components/app/page_1/Page1";
import "./App.css";

class App extends Component {
  state = {
    home: false,
    slides: [],
    filters: [{ title: "Section 1" }, { title: "Section 2" }],
    menuOptions: [],
    activePage: 0
  };

  componentDidMount() {
    this.getData();
  }

  async getData() {
    let responses = [],
      homeUrl = "https://voda-react-assessment.herokuapp.com/home",
      sliderUrl = "https://voda-react-assessment.herokuapp.com/slider",
      menuUrl = "https://voda-react-assessment.herokuapp.com/menu";

    const requestHome = axios.get(homeUrl);
    const requestSlider = axios.get(sliderUrl);
    const requestMenu = axios.get(menuUrl);

    try {
      responses = await axios.all([requestHome, requestSlider, requestMenu]);

      this.setState({ home: responses[0].data[0] });
      this.setState({ slides: responses[1].data });
      this.setState({ menuOptions: responses[2].data });
    } catch (err) {
      console.error(err);
    }
  }

  getActivePage() {
    let state = this.state,
      activePage = state.activePage;

    switch (activePage) {
      case 0:
        return;
      case 1:
        return;
      default:
        return <Home home={state.home} filters={state.filters} />;
    }
  }

  handleNavClick = (e, pageId) => {
    this.setState({ activePage: pageId });
  };

  render() {
    const state = this.state;

    return (
      <Router>
        <div className="App">
          <Header
            slides={state.slides}
            menuOptions={state.menuOptions}
            onNavOptionClick={this.handleNavClick}
          />
          <Switch>
            <Route path="/home">
              <Home home={state.home} filters={state.filters} />
            </Route>
            <Route path="/page1">
              <Page1 />
            </Route>
            <Route path="/">
              <Home home={state.home} filters={state.filters} />
            </Route>
          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
