import React, { Component } from "react";
import "./Home.css";
import NavBar from "../navbar/NavBar";
import FirstSection from "./first section/FirstSection";

class Home extends Component {
  state = { activeSection: 0 };

  handleFilterClick = (e, filterId) => {
    this.setState({ activeSection: filterId });
  };

  getActiveSection() {
    let state = this.state,
      props = this.props,
      activeSection = state.activeSection;

    switch (activeSection) {
      case 0:
        return (
          <FirstSection
            images={props.home ? props.home.sections[0].images : []}
          />
        );
      case 1:
        return <div className="section-services">services</div>;
      default:
        return (
          <FirstSection
            images={props.home ? props.home.sections[0].images : []}
          />
        );
    }
  }

  render() {
    const props = this.props;

    return (
      <div className="home">
        <div className="home-content">
          <span>{props.home.description}</span>
          <NavBar
            navCls="filters"
            navOptionCls="filters-option"
            navDotCls="filters-option-dot"
            onNavOptionClick={this.handleFilterClick}
            options={props.filters}
          />
          {this.getActiveSection()}
        </div>
      </div>
    );
  }
}

export default Home;
