import React, { Component } from "react";
import axios from "axios";
import "./Header.css";

class Header extends Component {
  state = { menuOptions: [] };

  async componentDidMount() {
    this.getMenuOptions();
  }

  async getMenuOptions() {
    let response;
    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/menu"
      );
      this.setState({ menuOptions: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  render() {
    return (
      <div className="header">
        <nav className="navbar">
          {this.state.menuOptions.map(menuOption => {
            return (
              <a className="navbar-option">
                <i className="navbar-option-dot"></i>
                <span>{menuOption.title}</span>
              </a>
            );
          })}
        </nav>
      </div>
    );
  }
}

export default Header;
