import React, { Component } from "react";
import "./Portfolio.css";

class Portfolio extends Component {
  state = { sections: [], activeSection: 0 };

  componentDidMount() {
    this.getSections();
  }

  async getSections() {
    let response;

    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/home"
      );
      this.setState({ slides: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  render() {
    return <div className="portfolio">portfolio</div>;
  }
}

export default Portfolio;
