import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import "./NavBar.css";

class Navbar extends Component {
  state = { activeOption: 0 };

  generateId() {
    return Math.random()
      .toString(36)
      .substr(2, 9);
  }

  onOptionClick(e, optionId) {
    this.setState({ activeOption: optionId });
    this.props.onNavOptionClick(e, optionId);
  }

  render() {
    return (
      <nav className="navbar">
        <ul>
          {this.props.options.map((option, index) => {
            return (
              <li key={this.generateId()}>
                <NavLink to="/page1" activeClassName="active">
                  {option.title}
                  <div className="navbar-option-dot"></div>
                </NavLink>
              </li>
            );
          })}
        </ul>
      </nav>
    );
  }
}

export default Navbar;
