import React, { Component } from "react";
import axios from "axios";
import "./Header.css";
import NavBar from "../navbar/NavBar";

class Header extends Component {
  state = { slides: [], activeSlide: 0 };

  componentDidMount() {
    this.getSlides();
  }

  async getSlides() {
    let response;
    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/slider"
      );
      this.setState({ slides: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  render() {
    const state = this.state,
      slides = state.slides;

    return (
      <div className="header">
        <NavBar options={state.menuOptions} />
        <span className="slider-title">
          {slides.length ? slides[state.activeSlide].title : ""}
        </span>
        <span className="slider-subtitle">
          {slides.length ? slides[state.activeSlide].subtitle : ""}
        </span>
        <div className="slider-dot-holder">
          {slides.length
            ? slides.map((slide, index) => {
                return (
                  <div className="slider-dot" key={"slider-dot-" + index}></div>
                );
              })
            : null}
        </div>
      </div>
    );
  }
}

export default Header;
