import React, { Component } from "react";
import axios from "axios";
import Header from "../home/header/Header";
import Portfolio from "../home/portfolio/Portfolio";
import "./Home.css";

class Home extends Component {
  state = { slides: [], sections: [], menuOptions: [] };

  componentDidMount() {
    this.getSlides();
    this.getSections();
    this.getMenuOptions();
  }

  async getSlides() {
    let response;

    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/slider"
      );
      this.setState({ slides: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  async getSections() {
    let response;

    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/home"
      );
      this.setState({ sections: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  async getMenuOptions() {
    let response;

    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/menu"
      );
      this.setState({ menuOptions: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  async sendRequest(endpoint) {
    let response;

    try {
      response = await axios.get(
        `https://voda-react-assessment.herokuapp.com/${endpoint}`
      );
      this.setState({ slides: response.data });
    } catch (err) {
      console.error(err);
    }
  }
  render() {
    const state = this.state;

    return (
      <div className="home">
        <Header slides={state.slides} menuOptions={state.menuOptions} />
        <Portfolio sections={state.sections} />
      </div>
    );
  }
}

export default Home;
