import React, { Component } from "react";
import axios from "axios";
import "./Header.css";
import NavBar from "./navbar/NavBar";

class Header extends Component {
  state = { slides: [], activeSlide: 0 };

  componentDidMount() {
    this.getSlides();
  }

  async getSlides() {
    let response;
    try {
      response = await axios.get(
        "https://voda-react-assessment.herokuapp.com/slider"
      );
      this.setState({ slides: response.data });
    } catch (err) {
      console.error(err);
    }
  }

  render() {
    const state = this.state,
      slides = state.slides;

    return (
      <div className="header">
        <NavBar options={state.menuOptions} />
        <span>{slides.length ? slides[state.activeSlide].title : ""}</span>
        <span>{slides.length ? slides[state.activeSlide].subtitle : ""}</span>
        <div>
          {slides.length
            ? slides.map(slide => {
                return <span className="slides-dot">dot</span>;
              })
            : null}{" "}
        </div>
      </div>
    );
  }
}

export default Header;
