import React, { Component } from "react";
import "./NavBar.css";

class Navbar extends Component {
  state = { activeOption: 0 };

  generateId() {
    return Math.random()
      .toString(36)
      .substr(2, 9);
  }

  onOptionClick(e, optionId) {
    this.setState({ activeOption: optionId });
    this.props.onNavOptionClick(e, optionId);
  }

  render() {
    return (
      <nav className={this.props.navCls}>
        <ul>
          {this.props.options.map((option, index) => {
            return (
              <li
                className={this.props.navOptionCls}
                key={this.generateId()}
                onClick={e => this.onOptionClick(e, option.id)}
              >
                {option.title}
                {this.state.activeOption === option.id ? (
                  <div className={this.props.navDotCls}></div>
                ) : null}
              </li>
            );
          })}
        </ul>
      </nav>
    );
  }
}

export default Navbar;
